export default async function actionSheetAutoCancelTest() {

  defineTest('actionSheetAutoCancelTestDemo', {
    level: TestCaseLevel.LEVEL0
  }, async () => {
    console.log('actionSheetAutoCancelTestDemo running')
    let driver: Tester = Tester.create()
    let button: Component =
      await driver.findComponent(Matcher.create().text('ActionSheetAutoCancelTest1').enabled(true))
    let button1: Component = await driver.findComponent(Matcher.create().text('ActionSheetAutoCancel1').enabled(true))

    await button.tap()
    let actionSheetAutoCancel1: Component =
      await driver.findComponent(Matcher.create().type('ActionSheet').enabled(true))
    let autoCancel1: string = await actionSheetAutoCancel1.getInspectorInfo()
    assertTrue(JSON.parse(autoCancel1).$attrs.autoCancel)
    await driver.tap(1, 1)

    await button1.tap()
    await button.tap()
    let actionSheetAutoCancel2: Component =
      await driver.findComponent(Matcher.create().type('ActionSheet').enabled(true))
    let autoCancel2: string = await actionSheetAutoCancel2.getInspectorInfo()
    assertFalse(JSON.parse(autoCancel2).$attrs.autoCancel)
    await driver.tap(1, 1)
  }, 0)
}

