          }
        })
      }.maxCount(3).columnsGap(10).rowsGap(10).padding(5)
    }.backgroundColor(Color.Gray)
  }

  build() {
    Column() {
      RichEditor(this.options)
        .customKeyboard(this.CustomKeyboardBuilder())
        .height(200)
        .id('richEditorCustomKeyboard')
    }
  }
}

