@Component
export struct ProgressRing {


  @Link @Watch("doneRateChanged") doneRate: number;

  @StorageLink('AccentColor1') @Watch('accentColorChanged') AccentColor1: string = '';
  @State accentColor: Resource = get_accent()
  @State ringDoneRate: number = this.doneRate;

  aboutToAppear() {
    this.accentColor = get_accent()
  }

  accentColorChanged() {
    this.accentColor = get_accent()
  }








  delay(ms) {
    return new Promise((resolve) => setTimeout(resolve, ms));
  }

  easeInOutQuad(s: number, t: number, x: number): number {
    return s + (t - s) * (x < 0.5 ? 2 * x * x : 1 - Math.pow(-2 * x + 2, 2) / 2);
  }

  async doneRateChanged() {
    let s = this.ringDoneRate
    let t = this.doneRate
    let temp = 0;
    var d_rate = 0.01;
    console.log("doneRateChanged ~", this.doneRate, this.ringDoneRate)
    for (let i = 0; i < 100; i++) {
      if (temp == 1) {
        this.ringDoneRate = this.doneRate;
        d_rate = 0;
        break;
      } else {
        temp += d_rate;
        this.ringDoneRate = this.easeInOutQuad(s, t, temp);
        console.log("doneRateChanged :", this.doneRate, this.ringDoneRate, temp)
        await this.delay(5);
      }
    }

  }

  build() {
    Column() {
      Stack() {
        Row() {
          Text((100 * this.ringDoneRate).toFixed(this.ringDoneRate == 1 ? 0 : 1).toString())
            .fontWeight(FontWeight.Medium)
            .fontSize(36)
            .fontColor($r('app.color.text_level1'))
          Text('%')
            .fontWeight(FontWeight.Medium)
            .fontSize(12)
            .fontColor($r('app.color.text_level2'))
        }
        .alignItems(VerticalAlign.Bottom)
        .offset({
          x: 4,
          y: 0
        })

        Progress({ value: 0, total: 100, type: ProgressType.Ring })
          .value(this.ringDoneRate * 100)
          .color(this.accentColor)
          .backgroundColor($r('app.color.barIcon_fill_off'))
          .style({ strokeWidth: 18 })
          .animation({
            delay: 200,
            duration: 200, 
            curve: Curve.Linear, 
          })
          .width('100%')
          .height('100%')

      }
      .height('100%')
      .width('100%')
    }
    .height(162)
    .width(162)
    .padding({ left: 6 })
  }
}

export default class DataTypeUtils {

  static isNull(obj: Object): boolean {
    return (typeof obj === 'undefined' || obj == null || obj === '');
  }


  static deepCopy(obj: number[]) {
    let newObj: number[] = [];
    for (let i = 0; i < obj.length; i++) {
      newObj[i] = JSON.parse(JSON.stringify(obj[i]));
    }
    return newObj;
  }
}

import display from '@ohos.display';
import { GlobalContext } from './GlobalContext';

let context = getContext(this);


const DESIGN_WIDTH = 360;


const DESIGN_HEIGHT = 780;


export default class DimensionUtil {

  static adaptDimension(value: number): number {
    let deviceDisplay = GlobalContext.getContext().getObject('globalDisplay') as display.Display;
    let widthScale = deviceDisplay.width / DESIGN_WIDTH;
    let virtualHeight = widthScale * DESIGN_HEIGHT;
    let designDim = Math.sqrt(DESIGN_WIDTH * DESIGN_WIDTH + DESIGN_HEIGHT * DESIGN_HEIGHT);
    let virtualDim = Math.sqrt(deviceDisplay.width * deviceDisplay.width + virtualHeight * virtualHeight);
    return virtualDim * value / designDim; 
  }


  static getPx(value: Resource): number {
    let beforeVp = context.resourceManager.getNumber(value.id);
    return DimensionUtil.adaptDimension(beforeVp);
  }


  static getVp(value: Resource): number {
    let beforeVp = context.resourceManager.getNumber(value.id);
    return px2vp(DimensionUtil.adaptDimension(beforeVp));
  }


  static getFp(value: Resource): number {
    let beforeFp = context.resourceManager.getNumber(value.id);
    return px2fp(DimensionUtil.adaptDimension(beforeFp));
  }
}

export class GlobalContext {
  private constructor() {
  }

  private static instance: GlobalContext;
  private _objects = new Map<string, Object>();

  public static getContext(): GlobalContext {
    if (!GlobalContext.instance) {
      GlobalContext.instance = new GlobalContext();
    }
    return GlobalContext.instance;
  }

  getObject(value: string): Object | undefined {
    return this._objects.get(value);
  }

  setObject(key: string, objectClass: Object): void {
    this._objects.set(key, objectClass);
  }
}

