class CheckEmptyUtils {

  isEmptyObj(obj: object | string) {
    return (typeof obj === 'undefined' || obj === null || obj === '');
  }


  isEmptyStr(str: string) {
    return str.trim().length === 0;
  }


  isEmptyArr(arr: Array<string>) {
    return arr.length === 0;
  }
}

