import router from '@ohos.router';
import { PayOrder } from '@ohos/orderdetail';
import { LocalDataManager, Order } from '@ohos/common';

@Entry
@Component
struct PayOrderPage {
  @Provide('orderList') data: Order[] = [];
  @State totalPrice: number = 0;
  private localDataManager: LocalDataManager = LocalDataManager.instance();

  aboutToAppear() {
    let params = router.getParams() as Record<string, Object>;
    let order: Order = params.order as Order;
    let orderIds: string[] = params.orderIds as string[];
    if (order) {
      this.data = [order];
    }
    if (orderIds) {
      const orders = this.localDataManager.queryOrderList();
      this.data = orders.filter(order => orderIds.includes(order.orderId));
    }
    this.totalPrice = this.data.reduce((sum, i) => {
      return sum + i.price * i.count;
    }, 0)
  }

  build() {
    Column() {
      PayOrder({
        totalPrice: $totalPrice
      })
    }
  }
}

import router from '@ohos.router';
import { StyleConstants, Logger, BreakpointSystem, BreakPointType } from '@ohos/common';
import { PageConstants } from '../constants/PageConstants';

@Entry
@Component
struct SplashPage {
  @StorageProp('currentBreakpoint') currentBreakpoint: string = 'sm';
  private breakpointSystem = new BreakpointSystem();
  private timeOutId: number = 0;

  build() {
    Flex({direction: FlexDirection.Column, alignItems: ItemAlign.Center}){
      Column() {
        Image($r('app.media.ic_eshop'))
          .width(new BreakPointType({
            sm: $r('app.float.splash_image_size'),
            md: $r('app.float.splash_image_size'),
            lg: $r('app.float.splash_image_size_lg')
          }).getValue(this.currentBreakpoint))
          .aspectRatio(PageConstants.IMAGE_ASPECT_RATIO)
          .objectFit(ImageFit.Contain)
      }
      .justifyContent(FlexAlign.Center)
      .alignItems(HorizontalAlign.Center)
      .flexGrow(StyleConstants.FLEX_GROW)

      Image($r('app.media.ic_title'))
        .width($r('app.float.text_image_width'))
        .height($r('app.float.text_image_height'))
        .objectFit(ImageFit.Contain)
      Text($r('app.string.splash_desc'))
        .fontColor($r('app.color.forty_alpha_black'))
        .fontSize($r('app.float.bigger_font_size'))
        .letterSpacing(PageConstants.LETTER_SPACE)
        .margin({ top: $r('app.float.vp_twelve'), bottom: $r('app.float.splash_text_margin_bottom') })
    }
    .height(StyleConstants.FULL_HEIGHT)
    .width(StyleConstants.FULL_WIDTH)
    .backgroundColor($r('app.color.page_background'))
  }

  aboutToAppear() {
    this.breakpointSystem.register();
    this.timeOutId = setTimeout(() => {
      router.replaceUrl({ url: PageConstants.MAIN_PAGE_URL })
        .catch((err: Error) => {
          Logger.error(JSON.stringify(err));
        })
    }, PageConstants.DELAY_TIME);
  }

  aboutToDisappear() {
    this.breakpointSystem.unregister();
    clearTimeout(this.timeOutId);
  }
}

import display from '@ohos.display';
import { CommonConstants } from '../constants/CommonConstants';

export class DeviceScreen {

  public static getDeviceHeight(): number {
    let displayObject = display.getDefaultDisplaySync();
    let screenPixelHeight = displayObject.height;
    let screenDensityDPI = displayObject.densityDPI;
    return screenPixelHeight * (CommonConstants.SCREEN_DPI_CONSTANT / screenDensityDPI);
  }


  public static getDeviceWidth(): number {
    let displayObject = display.getDefaultDisplaySync();
    let screenPixelWidth = displayObject.width;
    let screenDensityDPI = displayObject.densityDPI;
    return screenPixelWidth * (CommonConstants.SCREEN_DPI_CONSTANT / screenDensityDPI);
  }
}

import { BreakpointConstants, CommonConstants } from '@ohos/commons';
import { Home } from '../view/Home';

@Entry
@Component
struct Index {
  @StorageLink('currentBreakpoint') currentBreakpoint: string = BreakpointConstants.BREAKPOINT_LG;
  @StorageLink('videoGridColumn') videoGridColumn: string = CommonConstants.VIDEO_GRID_COLUMNS[0];

  build() {
    GridRow({
      columns: {
        sm: BreakpointConstants.GRID_ROW_COLUMNS[2],
        md: BreakpointConstants.GRID_ROW_COLUMNS[0],
        lg: BreakpointConstants.GRID_ROW_COLUMNS[0]
      }
    }) {
      GridCol({ span: {
        sm: BreakpointConstants.GRID_COLUMN_SPANS[5],
        md: BreakpointConstants.GRID_COLUMN_SPANS[0],
        lg: BreakpointConstants.GRID_COLUMN_SPANS[0]
      } }) {
        Home()
      }
      .height(CommonConstants.FULL_PERCENT)
      .width(CommonConstants.FULL_PERCENT)
    }
    .onBreakpointChange((breakPoints) => {
      this.currentBreakpoint = breakPoints;

      switch (breakPoints) {
        case BreakpointConstants.BREAKPOINT_SM:
          this.videoGridColumn = CommonConstants.VIDEO_GRID_COLUMNS[0];
          break;
        case BreakpointConstants.BREAKPOINT_MD:
          this.videoGridColumn = CommonConstants.VIDEO_GRID_COLUMNS[1];
          break;
        case BreakpointConstants.BREAKPOINT_LG:
          this.videoGridColumn = CommonConstants.VIDEO_GRID_COLUMNS[2];
          break;
        default:
          break;
      }
    })
  }
}

import { BreakpointConstants, CommonConstants } from '@ohos/commons';
import { BreakpointType } from '@ohos/commons';
import { HomeConstants } from '../constants/HomeConstants';
import { IconViewModel, VideoIcon } from '../viewmodel/IconViewModel';
import { getTabIndex } from './CommonView';

@Component
export struct IconView {
  @StorageLink('currentBreakpoint') currentBreakpoint: string = BreakpointConstants.BREAKPOINT_LG;
  @StorageLink('windowWidth') windowWidth: number = 0;
  @Prop currentTopIndex: number = 0;
  private iconList: VideoIcon[] = new IconViewModel().getIconList();

  @Styles focusedStyles(): void {
    .border({
      width: $r('app.float.focus_radius_width'),
      color: $r('app.color.focus_radius_color'),
      radius: $r('app.float.icon_focus_radius')
    })
  }

  @Styles normalStyles(): void {
    .border({
      width: 0,
      radius: $r('app.float.icon_focus_radius')
    })
  }

  build() {

    Column() {
      Swiper() {
        ForEach(this.iconList, (item: VideoIcon, index: number) => {
          Column() {
            Image(item.getImage())
              .width($r('app.float.icon_img_size'))
              .height($r('app.float.icon_img_size'))
              .margin({ bottom: $r('app.float.icon_img_bottom_margin') })
              .focusable(true)
              .groupDefaultFocus(index === 0 ? true : false)
              .stateStyles({
                normal: this.normalStyles,
                focused: this.focusedStyles
              })

            Text(item.getName())
              .fontSize($r('app.float.icon_list_font_size'))
              .fontWeight(FontWeight.Normal)
              .lineHeight($r('app.float.icon_list_font_height'))
              .fontColor(this.currentTopIndex === 2 ? Color.White : Color.Black)
          }
          .alignItems(HorizontalAlign.Center)
          .height($r('app.float.icon_list_height'))
        }, (item: VideoIcon) => JSON.stringify(item))
      }
      .tabIndex(getTabIndex(HomeConstants.DIRECTION_LIST[3]))

      .displayCount(new BreakpointType(CommonConstants.FOUR, HomeConstants.SIX, HomeConstants.EIGHT)
        .getValue(this.currentBreakpoint))
      .nextMargin(this.getNextMargin(this.windowWidth, this.currentBreakpoint))
      .indicator(false)
      .width(CommonConstants.FULL_PERCENT)
      .loop(false)
      .effectMode(EdgeEffect.None)
      .displayMode(SwiperDisplayMode.AUTO_LINEAR)
    }
    .padding({
      left: this.currentBreakpoint === BreakpointConstants.BREAKPOINT_LG ? $r('app.float.icon_list_column_padding_lg') :
        $r('app.float.icon_list_column_padding')
    })
    .margin({ top: this.currentBreakpoint === BreakpointConstants.BREAKPOINT_LG ?
      $r('app.float.icon_list_column_margin_lg') : $r('app.float.icon_list_column_margin') })
    .visibility(this.currentBreakpoint === BreakpointConstants.BREAKPOINT_LG && this.currentTopIndex === 2 ?
      Visibility.None : Visibility.Visible)
  }

  getNextMargin(windowWidth: number, currentBreakpoint: string): string {

    let result: number = px2vp(windowWidth) - new BreakpointType(HomeConstants.VIDEO_GRID_MARGIN[0],
      HomeConstants.VIDEO_GRID_MARGIN[1], HomeConstants.VIDEO_GRID_MARGIN[2]).getValue(this.currentBreakpoint) /
      HomeConstants.TWO;
    if (currentBreakpoint === BreakpointConstants.BREAKPOINT_SM) {
      result = result / HomeConstants.DISPLAYED_ICON_NUMBER[0];
    } else if (currentBreakpoint === BreakpointConstants.BREAKPOINT_MD) {
      result = result / HomeConstants.DISPLAYED_ICON_NUMBER[1];
    } else {
      result = (result - HomeConstants.LG_SIDEBAR_WIDTH) / HomeConstants.DISPLAYED_ICON_NUMBER[2];
    }
    return result + HomeConstants.HEIGHT_UNIT;
  }
}

import { BreakpointConstants, CommonConstants } from '@ohos/commons';
import { BreakpointType } from '@ohos/commons';
import { HomeConstants } from '../constants/HomeConstants';
import { VideoImage, VideoImgViewModel } from '../viewmodel/VideoImgViewModel';
import { getTabIndex,SubtitleComponent,VideoContent,VideoImgComponent, VideoImgPlay, VideoImgRating, VideoTitle } from
  './CommonView';

@Component
export struct NewVideoRelease {
  @StorageLink('currentBreakpoint') currentBreakpoint: string = BreakpointConstants.BREAKPOINT_LG;
  @StorageLink('windowWidth') windowWidth: number = 0;
  @Prop currentTopIndex: number = 0;
  private newVideoImgList: VideoImage[] = new VideoImgViewModel().getNewVideoImgList();

  build() {
    Column() {
      SubtitleComponent({ currentTopIndex: this.currentTopIndex, title:HomeConstants.HOME_SUB_TITLES[0] })


      Grid() {
        ForEach(this.newVideoImgList, (item: VideoImage) => {
          GridItem() {
            Column() {
              Stack({ alignContent: Alignment.Bottom }) {
                VideoImgComponent({ imgSrc:item.getImgSrc() })
                VideoImgPlay()
                VideoImgRating({ rating: item.getRating() })
              }
              .height(new BreakpointType($r('app.float.new_video_height_sm'), $r('app.float.new_video_height_md'),
                $r('app.float.new_video_height_lg')).getValue(this.currentBreakpoint))
              .width(CommonConstants.FULL_PERCENT)
              .aspectRatio(HomeConstants.NEW_VIDEO_ASPECT_RATIO)

              VideoTitle({ currentTopIndex: this.currentTopIndex, title: item.getTitle() })
              VideoContent({ currentTopIndex: this.currentTopIndex, content: item.getContent() })
            }
            .alignItems(HorizontalAlign.Start)
          }
        }, (item: VideoImage, index: number) => index + JSON.stringify(item))
      }
      .tabIndex(getTabIndex(HomeConstants.DIRECTION_LIST[5]))
      .columnsTemplate(new BreakpointType(CommonConstants.VIDEO_GRID_COLUMNS[1], CommonConstants.VIDEO_GRID_COLUMNS[3],
        CommonConstants.VIDEO_GRID_COLUMNS[4]).getValue(this.currentBreakpoint))
      .rowsTemplate(HomeConstants.NEW_VIDEO_ROWS_TEMPLATE)
      .width(CommonConstants.FULL_PERCENT)
      .columnsGap($r('app.float.new_video_columns_gap'))
      .height(this.getNewVideoHeight(this.currentBreakpoint, this.windowWidth))
    }
    .padding({ left: this.currentBreakpoint === BreakpointConstants.BREAKPOINT_LG && this.currentTopIndex === 2 ?
      $r('app.float.side_bar_width') : 0 })
  }

  getNewVideoHeight(currentBreakpoint: string, windowWidth: number): string {
    let result: number = px2vp(windowWidth) - new BreakpointType(HomeConstants.VIDEO_GRID_MARGIN[0],
      HomeConstants.VIDEO_GRID_MARGIN[1], HomeConstants.VIDEO_GRID_MARGIN[2]).getValue(this.currentBreakpoint);
    if (currentBreakpoint === BreakpointConstants.BREAKPOINT_LG) {
      result = result - HomeConstants.LG_SIDEBAR_WIDTH;
      result = (result - HomeConstants.VIDEO_GRID_ITEM_SPACE * HomeConstants.SIX) / HomeConstants.SEVEN;
    } else if (currentBreakpoint === BreakpointConstants.BREAKPOINT_MD) {
      result = (result - HomeConstants.VIDEO_GRID_ITEM_SPACE * CommonConstants.FOUR) / HomeConstants.FIVE;
    } else {
      result = (result - HomeConstants.VIDEO_GRID_ITEM_SPACE * HomeConstants.TWO) / CommonConstants.THREE;
    }
    return (result / HomeConstants.NEW_VIDEO_ASPECT_RATIO + HomeConstants.NEW_VIDEO_DESCRIPTION_HEIGHT +
      HomeConstants.HEIGHT_UNIT);
  }
}

