class NumberUtil {
  _parseInt(str: string, radix: number): number {
    let res = 0;

    str = String(str).trim().split('.')[0]
    let length = str.length;

    for (let i = 0; i < length; i++) {
      let arr = str.split('').reverse().join('');
      res += Math.floor(Number(arr[i])) * Math.pow(radix, i)
    }

    return res;
  }
}

