import ButtonViewModel from '../../viewmodel/ButtonsViewModel';

@Component
export struct Buttons {
  @Prop buttonText: string = '';
  @Prop stateEffect: boolean = false;
  @Prop buttonShape: string = '';
  @Prop buttonType: string = '';
  @Prop fontColor: string = '';

  build() {
    Row() {
      Column() {
        Button({
          type: ButtonViewModel.fetchType(this.buttonShape),
          stateEffect: this.stateEffect
        }) {
          Text(this.buttonText)
            .fontSize($r('app.float.default_16'))
            .fontColor(this.fontColor || $r('app.color.white'))
        }
        .width($r('app.float.default_90'))
        .height($r('app.float.default_35'))
        .backgroundColor(ButtonViewModel.fetchBackgroundColor(this.buttonType))
      }
    }
  }
}

class ButtonViewModel {
  fetchType(shape: string) {
    let ret: ButtonType;
    switch (shape) {
      case 'capsule':
        ret = ButtonType.Capsule;
        break
      case 'circle':
        ret = ButtonType.Circle;
        break
      case 'normal':
        ret = ButtonType.Normal;
        break
      default:
        ret = ButtonType.Capsule;
        break
    }
    return ret
  }

  fetchBackgroundColor(type: string) {
    let ret: Resource;
    switch (type) {
      case 'success':
        ret = $r('app.color.success');
        break
      case 'info':
        ret = $r('app.color.info');
        break
      case 'warning':
        ret = $r('app.color.warning');
        break
      case 'danger':
        ret = $r('app.color.danger');
        break
      case 'none':
        ret = $r('app.color.none');
        break
      default:
        ret = $r('app.color.normal');
        break
    }
    return ret
  }
}

