        .layoutWeight(1)

        Image($r('app.media.ic_next'))
          .width($r('app.float.control_image_width'))
          .aspectRatio(CommonConstants.ASPECT_RATIO)
          .onClick(() => {
            if (this.playVideoModel !== null) {
              this.playVideoModel!.nextVideo();
