export class SplashSource implements IDataSource {
  private splashArray: Resource[] = [];
  private listeners: DataChangeListener[] = [];

  public setDataArray(dataArray: Resource[]): void {
    this.splashArray = dataArray;
  }

  totalCount(): number {
    return this.splashArray.length;
  }

  getData(index: number): Resource {
    return this.splashArray[index];
  }

  registerDataChangeListener(listener: DataChangeListener): void {
    if (this.listeners.indexOf(listener) < 0) {
      this.listeners.push(listener);
    }
  }

  unregisterDataChangeListener(listener: DataChangeListener): void {
    let pos: number = this.listeners.indexOf(listener);
    if (pos >= 0) {
      this.listeners.splice(pos, 1);
    }
  }
}

