        this.area_width = n.width as number;
      })
      .columnsTemplate("1fr ".repeat(Math.ceil(this.area_width / 70)))
      .layoutWeight(1)
      .edgeEffect(EdgeEffect.Spring)
      .scrollBar(BarState.On)

      Row({ space: 10 }) {
        TextInput({ text: this.requestSymbolName })
          .onChange((a) => {
            this.requestSymbolName = a;
          })
          .onSubmit(() => {
            this.refresh_visibility_statuses();
          })
          .layoutWeight(1)
        SymbolGlyph($r('sys.symbol.magnifyingglass'))
          .fontSize(fontSize_Icon_Button())
          .fontWeight(FontWeight.Bold)
          .fontColor([$r('app.color.color_accent')])
          .onClick(() => {
            this.refresh_visibility_statuses();
          })
      }

    }
    .alignItems(HorizontalAlign.Start)
    .padding({ left: 20, right: 20, top: 10 })
  }

  fill_zeros_to_string(num: number, length: number) {
    return String("0".repeat(Math.max(length - num.toString().length, 0)) + num.toString());
  }

  refresh_visibility_statuses() {
    if (this.requestSymbolName == "") {
      this.allSymbols = symbolsNames;
      return;
    }
    this.allSymbols = [];
    for (let index = 0; index < symbolsNames.length; index++) {
      if (symbolsNames[index].includes(this.requestSymbolName)) {
        this.allSymbols.push(symbolsNames[index])
      }
    }
  }
}

import { MiniCanvasAttribute } from "./canvas/attribte"
import { CanvasImpl } from "./canvas/canvas"
import { ICanvas } from "./canvas/icanvas"

@ComponentV2
export struct MiniCanvas {

  private canvas?: CanvasImpl = undefined

  @Param
  attribute: MiniCanvasAttribute = MiniCanvasAttribute.default()

  @Param @Require
  onDraw : (canvas: ICanvas) => void = (_canvas: ICanvas) => {}

  build() {
    Canvas(this.canvas?.renderContext)
      .width(this.attribute.width)
      .height(this.attribute.height)
      .backgroundColor(this.attribute.background)
      .id(this.attribute.id)
      .enabled(this.attribute.enabled)
      .enableAnalyzer(this.attribute.enableAnalyzer)
      .onReady(() => {
        if (this.canvas) {
          this.onDraw(this.canvas)
        }
      })
      .onClick((event) => {
        if (this.attribute.clickListener) {
          this.attribute.clickListener(event)
        }
      })
      .onTouch((event) => {
        if (this.attribute.touchListener) {
          this.attribute.touchListener(event)
        }
      })
      .onMouse((event) => {
        if (this.attribute.mouseListener) {
          this.attribute.mouseListener(event)
        }
      })
      .onHover((isHover: boolean, event: HoverEvent) => {
        if (this.attribute.hoverListener) {
          this.attribute.hoverListener(isHover, event)
        }
      })
  }

  aboutToAppear(): void {
    let setting = new RenderingContextSettings(this.attribute.antiAlias);
    let context = new CanvasRenderingContext2D(setting);
    this.canvas = new CanvasImpl(context, this.attribute);
  }
}

import { LightWeightMap } from "@kit.ArkTS";


export class Gradient {

  readonly x0: number;
  readonly y0: number;
  readonly x1: number;
  readonly y1: number;

  private readonly colorStopMap: LightWeightMap<number, string> = new LightWeightMap()

  constructor(x0: number, y0: number, x1: number, y1: number) {
    this.x0 = x0;
    this.y0 = y0;
    this.x1 = x1;
    this.y1 = y1;
  }


  linearGradient(offset: number, color: string): Gradient {
    return this.addColorStop(offset, color)
  }


  addColorStop(offset: number, color: string): Gradient {
    this.colorStopMap.set(offset, color)
    return this
  }

  getColorStop(): LightWeightMap<number, string> {
    return this.colorStopMap
  }
}

