  @State data: any[] = [];
  @State pn: number = 1;

  build() {
    Column() {
      HomePullToRefresh({

        data: $data,

        scroller: this.scroller,

        customList: this.getListView,

        onRefresh: () => {
          return new Promise<string>((resolve, reject) => {
            this.pn = 1;
            ApiManager.getInstance().requestPopular(this.pn).then((resp: boolean) => {
              resolve('');
            });
          });
        },

        onLoadMore: () => {
