import { audio } from '@kit.AudioKit';
import { fileIo } from '@kit.CoreFileKit';


class Options {
  offset?: number;
  length?: number;
}


class AudioRendererManager {

  private static audioRender: audio.AudioRenderer | null = null


  static async doPlay(fileName: string) {
    await AudioRendererManager.ready(fileName)
    AudioRendererManager.start()
  }


  private static async ready(fileName: string) {

    try {
      let bufferSize: number = 0;
      let audioStreamInfo: audio.AudioStreamInfo = {
        samplingRate: audio.AudioSamplingRate.SAMPLE_RATE_16000, 
        channels: audio.AudioChannel.CHANNEL_1, 
        sampleFormat: audio.AudioSampleFormat.SAMPLE_FORMAT_S16LE, 
        encodingType: audio.AudioEncodingType.ENCODING_TYPE_RAW 
      }
      let audioRendererInfo: audio.AudioRendererInfo = {
        usage: audio.StreamUsage.STREAM_USAGE_MUSIC, 
        rendererFlags: 0 
      }
      let audioRendererOptions: audio.AudioRendererOptions = {
        streamInfo: audioStreamInfo,
        rendererInfo: audioRendererInfo
      }
      let path = getContext().filesDir;

      let file: fileIo.File = fileIo.openSync(fileName, fileIo.OpenMode.READ_ONLY);
      const fileSize = fileIo.statSync(file.path).size
      let writeDataCallback = (buffer: ArrayBuffer) => {
        let options: Options = {
          offset: bufferSize,
          length: buffer.byteLength
        }
        fileIo.readSync(file.fd, buffer, options);
        bufferSize += buffer.byteLength;

        if (bufferSize >= fileSize) {
          AudioRendererManager.stop()
            .then(() => {
              AudioRendererManager.release()
            })
        }
      }
      AudioRendererManager.audioRender = await audio.createAudioRenderer(audioRendererOptions)
      AudioRendererManager.audioRender.on("writeData", writeDataCallback)
    } catch (e) {
      console.log("e", e.message, e.code)
    }
  }


  static async start() {

    await AudioRendererManager.audioRender?.start();
  }


  static async pause() {
    await AudioRendererManager.audioRender?.pause()
  }


  static async stop() {
    await AudioRendererManager.audioRender?.stop()
  }


  static async release() {
    await AudioRendererManager.audioRender?.release()
  }
}

export default AudioRendererManager

import { abilityAccessCtrl, bundleManager, common, Permissions } from '@kit.AbilityKit';



export class PermissionUtils {

  static check(permissions: Permissions[]): boolean {

    const atManager = abilityAccessCtrl.createAtManager();

    const bundleInfo = bundleManager.getBundleInfoForSelfSync(bundleManager.BundleFlag.GET_BUNDLE_INFO_WITH_APPLICATION)

    const tokenID = bundleInfo.appInfo.accessTokenId

    const authResults = permissions.map((item) => atManager.checkAccessTokenSync(tokenID, item))

    return authResults.every(v => v === abilityAccessCtrl.GrantStatus.PERMISSION_GRANTED)
  }


  static async request(permissions: Permissions[]): Promise<boolean> {
    if (PermissionUtils.check(permissions)) {
      return true
    }

    const atManager = abilityAccessCtrl.createAtManager();

    const grantStatus = await atManager.requestPermissionsFromUser(getContext(), permissions)

    const isAuth = grantStatus.authResults.every(v => v === abilityAccessCtrl.GrantStatus.PERMISSION_GRANTED)

    return isAuth
  }


  static openPermissionSettingsPage() {

    const context = getContext() as common.UIAbilityContext

    const bundleInfo = bundleManager.getBundleInfoForSelfSync(bundleManager.BundleFlag.GET_BUNDLE_INFO_WITH_APPLICATION)

    context.startAbility({
      bundleName: 'com.huawei.hmos.settings',
      abilityName: 'com.huawei.hmos.settings.MainAbility',
      uri: 'application_info_entry',
      parameters: {

        pushParams: bundleInfo.name
      }
    })
  }
}

import { promptAction } from '@kit.ArkUI';

export class ToastUtils {
  static show(msg: string) {
    ToastUtils.showTime(msg, 2000)
  }

  static showLong(msg: string) {
    ToastUtils.showTime(msg, 4000)
  }

  static showTime(msg: string, duration: number = 4000) {
    try {
      promptAction.showToast({
        message: msg,
        duration: duration,
        bottom: "center",
      })
    } catch (error) {
      console.error(`showToast args error code is ${error.code}, message is ${error.message}`);
    }
  }
}
